import React, {useState} from "react";

export default function WeatherTemperature(props) {
    const [unit, setUnit] = useState("celsius");

    function convertToFar(event){
        event.preventDefault();
        setUnit("Far")
    }

    function convertToCel(event){
        event.preventDefault();
        setUnit("Cel")
    }

    if (unit === "celsius") {
        return (<span className="WeatherTemperature">
        <span className="main-temperature">{Math.round(props.celsius)}</span>

                <span className="units">째C | <a href="/" onClick={convertToFar}>째F</a></span>

        </span>
        );

    } else {
        return (<span className="WeatherTemperature">
        <span className="main-temperature">{Math.round(props.celsius)}</span>

                <span className="units"><a href="/" onClick={convertToCel}>째C</a> | 째F</span>

        </span>

        );
    }}
